// Step 1: Remove the triple backticks and any surrounding whitespace
const cleaned = input.replace(/```json|```/g, '').trim();

// Step 2: Parse JSON
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (error) {
  throw new Error(`Failed to parse JSON: ${error.message}`);
}

// Step 3: Return as an array "trend_signals"
return [
  {
    json: {
      trend_signals: [
        parsed.trend_signal,
        parsed.competitor_signal,
        parsed.style_signal
      ]
    }
  }
];

// Process all HTML from "data" property
const inputItems = $input.all();
const allProducts = [];

function extractProductData(html) {
  const product = {
    name: null,
    price: null,
    originalPrice: null,
    sizes: [],
    colors: [],
    description: null,
    productId: null,
    itemIndex: null // Use itemIndex instead of index
  };

  // Extract Product Name
  const nameMatch = html.match(/<h1[^>]*id="main-title"[^>]*>([^<]+)/i);
  if (nameMatch) product.name = nameMatch[1].trim();

  // Extract Current Price
  const priceMatch = html.match(/<span[^>]*itemprop="price"[^>]*>([^<]+)/i);
  if (priceMatch) {
    product.price = priceMatch[1].trim();
  }

  // Extract Original Price (if on sale)
  const originalPriceMatch = html.match(/Was\s*\$([0-9,.]+)/i);
  if (originalPriceMatch) {
    product.originalPrice = `$${originalPriceMatch[1]}`;
  }

  // Extract Sizes with individual prices
  const sizeMatches = [...html.matchAll(/variant-tile-price-text-([^"]+)"[^>]*>\$([0-9,.]+)/g)];
  sizeMatches.forEach(([, size, price]) => {
    const isOutOfStock = html.includes('tile-strikethrough');
    const isSelected = html.includes(`selected, ${size}`);

    product.sizes.push({
      size: size.trim(),
      price: `$${price}`,
      available: !isOutOfStock,
      selected: isSelected
    });
  });

  // Extract Colors
  const colorMatches = [...html.matchAll(/aria-label="([^"]+)"[^>]*role="img"/g)];
  colorMatches.forEach(([, color]) => {
    product.colors.push({
      color: color.trim(),
      selected: html.includes(`selected, ${color}`)
    });
  });

  // Extract Description
  const descStart = html.indexOf('<ul>', html.indexOf('About this item'));
  if (descStart !== -1) {
    const descEnd = html.indexOf('</ul>', descStart);
    if (descEnd !== -1) {
      product.description = html.substring(descStart, descEnd)
        .replace(/<[^>]*>/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();
    }
  }

  // Extract Product ID
  const idMatch = html.match(/\/ip\/[^\/]+\/(\d+)/);
  if (idMatch) product.productId = idMatch[1];

  return product;
}

