// Walmart product cards selector
$('div[data-testid="list-view"] a[href*="/ip/"]').each((i, el) => {
  const url = $(el).attr('href');
  if (url) {
    // Ensure absolute URL
    const fullUrl = url.startsWith('http') 
      ? url 
      : `https://www.walmart.com${url}`;

    // Add only if not already seen
    if (!seen.has(fullUrl)) {
      seen.add(fullUrl);
      results.push({ url: fullUrl });
    }
  }
});

// Return deduplicated results
