// n8n JavaScript Code to Process and Return All Data in One Array
// Combines all products and articles into a single array

const allData = [];

// Loop through all input items
for (const item of $input.all()) {
  let dataArray = item.json;

  // Handle if data is wrapped in a "data" property
  if (dataArray.data && Array.isArray(dataArray.data)) {
    dataArray = dataArray.data;
  }

  // If it's already an array, use it directly
  if (Array.isArray(dataArray)) {
    // Process each item in the array
    dataArray.forEach(data => {
      // Check if this is a products object (has products array)
      if (data.products && Array.isArray(data.products)) {
        // Process each product
        data.products.forEach(product => {
          if (!product.error) { // Skip failed scrapes
            allData.push({
              type: 'product',
              name: product.name,
              price: product.price,
              url: product.url,
              pattern: product.pattern,
              details: product.details,
              scraped_at: product.scraped_at,
              myNewField: 1,
              // Additional processing fields
              price_numeric: parseInt(product.price.replace(/[^\d]/g, '')) || 0,
              has_details: product.details && product.details.length > 0,
              category: product.name.toLowerCase().includes('mat') ? 'mat' : 
                       product.name.toLowerCase().includes('rug') ? 'rug' : 'carpet',
              detail_count: product.details ? product.details.length : 0
            });
          }
        });

        // Also add summary data for the batch
        allData.push({
          type: 'scrape_summary',
          total_scraped: data.total_scraped,
          successful_scrapes: data.successful_scrapes,
          failed_scrapes: data.failed_scrapes,
          batch_scraped_at: data.batch_scraped_at,
          myNewField: 1
        });
      }

      // Check if this is a news article
      else if (data.title && data.link) {
        allData.push({
          type: 'article',
          title: data.title,
          link: data.link,
          pubDate: data.pubDate,
          isoDate: data.isoDate,
          content: data.content,
          contentSnippet: data.contentSnippet,
          myNewField: 1,
          // Additional processing fields
          word_count: data.contentSnippet ? data.contentSnippet.split(' ').length : 0,
          has_content: Boolean(data.content),
          publish_date: new Date(data.isoDate),
          domain: data.link.split('/')[2] || data.link,
          source: data.link.split('/')[2] === 'corporate.target.com' ? 'Target' : 
                  data.link.split('/')[2] === 'ruginsider.com' ? 'Rug Insider' : 'Other'
        });
      }

      // Handle any other data structure
      else {
        allData.push({
          ...data,
          type: 'other',
          myNewField: 1,
          processed_at: new Date().toISOString()
        });
      }
    });
  }
  // If it's not an array, process as single item
  else {
    allData.push({
      ...dataArray,
      type: 'single_item',
      myNewField: 1,
      processed_at: new Date().toISOString()
    });
  }
}

// Return all data as a single array in one item
return [{ json: { allData: allData, totalItems: allData.length, processedAt: new Date().toISOString() } }];

