let html = $input.first().json.output;

// Remove markdown fences like ```html or ```
html = html.replace(/```html|```/g, "");

// Remove all \n, \r, \t, and excessive spaces
html = html.replace(/\\[nrt]/g, " ").replace(/\s{2,}/g, " ");

// Remove duplicate or misplaced <html> / <body> wrappers
html = html.replace(/<\/?body><\/?body>/g, "")
           .replace(/<\/?html><\/?html>/g, "")
           .replace(/<body><!DOCTYPE html>/g, "<!DOCTYPE html>");

// Trim leading/trailing spaces
html = html.trim();

// Optional: Ensure the final HTML starts and ends properly
if (!html.startsWith("<!DOCTYPE html>")) {
  html = `<!DOCTYPE html><html><body>${html}</body></html>`;
}

// Return clean minified HTML
return [{ json: { cleaned_minified_html: html } }];

for (const item of $input.all()) {
  // Remove duplicates from product_links array
  const uniqueLinks = [...new Set($input.first().json.product_links)];

  // Add base URL to make complete URLs
  const fullUrls = uniqueLinks.map(link => {
    if (link.startsWith('/')) {
      return `https://www.amazon.com${link}`;
    }
    return link; // in case it's already a full URL
  });

  // Update the item with processed URLs
  item.json.product_links = fullUrls;
}

return $input.all();

