const cheerio = require('cheerio');
const results = [];

for (const item of $input.all()) {
  try {
    const url = item.json.url;
    const html = item.json.data;
    const $ = cheerio.load(html);

    const name = $('#productTitle').text().trim();
    const price = $('span.a-price span.a-price-whole').first().text().trim();

    const details = [];
    $('#feature-bullets ul.a-unordered-list li span.a-list-item').each((i, el) => {
      const text = $(el).text().trim();
      if (text && text.length > 0) {
        details.push(text);
      }
    });

    const pattern = $('#inline-twister-expanded-dimension-text-pattern_name').text().trim();

    results.push({
      url,
      name,
      price,
      details,
      pattern,
      scraped_at: new Date().toISOString()
    });
  } catch (error) {
    console.error('Error processing item:', error);
    results.push({
      url: item.json.url || 'Unknown URL',
      name: '',
      price: '',
      details: [],
      pattern: '',
      error: error.message,
      scraped_at: new Date().toISOString()
    });
  }
}

// Return a single item containing all scraped data as an array
return [{
  json: {
    products: results,
    total_scraped: results.length,
    successful_scrapes: results.filter(r => !r.error).length,
    failed_scrapes: results.filter(r => r.error).length,
    batch_scraped_at: new Date().toISOString()
  }
}];
