for (const item of $input.all()) {
  // Remove duplicates from product_links array
  const uniqueLinks = [...new Set($input.first().json.product_links)];

  // Add base URL to make complete URLs
  const fullUrls = uniqueLinks.map(link => {
    if (link.startsWith('/')) {
      return `https://www.amazon.com${link}`;
    }
    return link; // in case it's already a full URL
  });

  // Update the item with processed URLs
  item.json.product_links = fullUrls;
}

